import request from './config'

// 文件系统相关 API
export const fileApi = {
    // 获取当前路径
    getCurrentPath: () => {
        return request.get('/file/current-path')
    },

    // 获取目录树
    getDirectoryTree: () => {
        return request.get('/file/tree')
    },

    // 创建目录
    createDirectory: (name) => {
        return request.post('/file/directory', { name })
    },

    // 创建文件
    createFile: (name) => {
        return request.post('/file', { name })
    },

    // 删除目录
    deleteDirectory: (name) => {
        return request.delete(`/file/directory/${name}`)
    },

    // 删除文件
    deleteFile: (name) => {
        return request.delete(`/file/${name}`)
    },

    // 打开文件
    openFile: (name) => {
        return request.post(`/file/${name}/open`)
    },

    // 关闭文件
    closeFile: (name) => {
        return request.post(`/file/${name}/close`)
    },

    // 读取文件内容
    readFileContent: (name) => {
        return request.get(`/file/${name}/content`)
    },

    // 写入文件内容
    writeFileContent: (name, content) => {
        return request.put(`/file/${name}/content`, { content })
    },

    // 切换目录
    changeDirectory: (path) => {
        return request.post('/file/change-directory', { path })
    },

    // 获取磁盘状态
    getDiskStatus: () => {
        return request.get('/file/disk-status')
    }
}
