import axios from 'axios';

// 创建 axios 实例
const instance = axios.create({
    baseURL: 'http://localhost:8080', // 设置基础URL，根据实际后端服务地址修改
    timeout: 5000, // 请求超时时间
    headers: {
        'Content-Type': 'application/json'
    }
});

// 请求拦截器
instance.interceptors.request.use(
    config => {
        // 在发送请求之前做些什么
        return config;
    },
    error => {
        // 对请求错误做些什么
        return Promise.reject(error);
    }
);

// 响应拦截器
instance.interceptors.response.use(
    response => {
        // 对响应数据做点什么
        return response;
    },
    error => {
        // 对响应错误做点什么
        console.error('API请求错误:', error);
        return Promise.reject(error);
    }
);

// 进程相关 API
export const processApi = {
    // 创建进程
    createProcess: (processData) => {
        return instance.post('/process', processData);
    },

    // 获取所有进程
    getAllProcesses: () => {
        return instance.get('/process');
    },

    // 获取特定状态的进程
    getProcessesByState: (state) => {
        return instance.get(`/process/${state}`);
    },

    // 获取当前运行进程
    getCurrentProcess: () => {
        return instance.get('/process/current');
    },

    // 删除进程
    deleteProcess: (id) => {
        return instance.delete(`/process/${id}`);
    },

    // 更新进程状态
    updateProcessState: (id, state) => {
        return instance.put(`/process/${id}/state`, { state });
    },

    // 终止进程
    terminateProcess: (id) => {
        return instance.post(`/process/${id}/terminate`);
    },

    // 更新进程优先级
    updateProcessPriority: (id, priority) => {
        return instance.put(`/process/${id}/priority`, { priority });
    },

    // 获取进程详细信息
    getProcessInfo: (id) => {
        return instance.get(`/process/${id}/info`);
    },

    // 获取进程统计信息
    getProcessStats: () => {
        return instance.get('/process/stats');
    },

    // 设置调度算法
    setSchedulingAlgorithm: (algorithm) => {
        return instance.put('/process/scheduling-algorithm', { algorithm });
    },

    // 获取当前调度算法
    getSchedulingAlgorithm: () => {
        return instance.get('/process/scheduling-algorithm');
    },

    // 批量更新进程优先级
    batchUpdatePriorities: (priorities) => {
        return instance.put('/process/batch-update-priorities', priorities);
    }
};

// 中断相关 API
export const interruptApi = {
    // 触发进程中断
    triggerInterrupt: (id, reason) => {
        return instance.post(`/process/${id}/interrupt`, { reason });
    }
};

// 设备相关 API
export const deviceApi = {
    // 请求设备
    requestDevice: (id, deviceType) => {
        return instance.post(`/process/${id}/request-device`, { deviceType });
    },

    // 释放设备
    releaseDevice: (id, deviceType) => {
        return instance.post(`/process/${id}/release-device`, { deviceType });
    }
};
